mapper.select:



SELECT * FROM (
   SELECT id, status, actual_start_time, execute_result FROM asgard_quartz_task_instance
   WHERE task_id = #{taskId}
   ORDER BY CREATION_DATE DESC
) WHERE ROWNUM <= 1

mapper.update:
UPDATE asgard_quartz_task_instance
SET instance_lock = #{instance}, object_version_number = object_version_number + 1

    , actual_start_time = #{time}

WHERE id = #{id} AND instance_lock IS NULL AND object_version_number = #{number}

mapper.select:
SELECT aqti.ID AS id, aqti. STATUS AS STATUS, aqti.ACTUAL_START_TIME AS actualStartTime, aqti.PLANNED_START_TIME AS plannedStartTime, concat( concat(tmp.SERVICE, ":"), aqti.INSTANCE_LOCK ) AS serviceInstanceId FROM asgard_quartz_task_instance aqti LEFT JOIN ( SELECT aqm.SERVICE, aqt.ID FROM asgard_quartz_method aqm LEFT JOIN asgard_quartz_task aqt ON aqm. CODE = aqt.EXECUTE_METHOD ) tmp ON aqti.TASK_ID = tmp.ID WHERE 1 = 1
AND aqti.TASK_ID = #{taskId}
AND aqti.STATUS LIKE concat(concat('%',#{status}),'%')
AND aqti.fd_level = #{level}
AND aqti.source_id = #{sourceId}
AND concat( concat(tmp.SERVICE, ":"), aqti.INSTANCE_LOCK ) LIKE concat(concat('%',#{serviceInstanceId}),'%')
AND( concat( concat(tmp.service, ":"), aqti.INSTANCE_LOCK ) LIKE concat(concat('%',#{params}),'%') OR status LIKE concat(concat('%',#{params}),'%') )
