mapper.select:
SELECT COUNT(*) FROM asgard_saga_instance asi


        AND asi.FD_LEVEL=#{level}


        AND asi.SOURCE_ID=#{sourceId}


        AND concat(concat(asi.saga_code, '-'), asi.id) LIKE concat(concat('%',#{sagaCode}),'%')




                AND (asi.status = #{status} OR asi.status = 'NON_CONSUMER')


                AND asi.status = #{status}




        AND asi.ref_type LIKE concat(concat('%',#{refType}),'%')


        AND asi.ref_id LIKE concat(concat('%',#{refId}),'%')


        AND (
        concat(concat(asi.saga_code, '-'), asi.id) LIKE concat(concat('%',#{params}),'%') OR
        asi.ref_type LIKE concat(concat('%',#{params}),'%') OR
        asi.ref_id LIKE concat(concat('%',#{params}),'%')
        )

SELECT asi.ID AS ID, asi.SAGA_CODE AS SAGA_CODE, ( SELECT aos.description FROM asgard_orch_saga aos WHERE aos. CODE = asi.SAGA_CODE ) AS description, ( SELECT aos.service FROM asgard_orch_saga aos WHERE aos. CODE = asi.SAGA_CODE ) AS service, asi.FD_LEVEL AS LEVEL, asi.REF_TYPE AS REF_TYPE, asi.REF_ID AS REF_ID, asi.START_TIME AS START_TIME, asi.END_TIME AS END_TIME, asi.STATUS AS STATUS, sum(CASE WHEN asti.STATUS = 'COMPLETED' THEN 1 ELSE 0 END) AS COMPLETED_COUNT, sum(CASE WHEN asti.STATUS = 'RUNNING' THEN 1 ELSE 0 END) AS RUNNING_COUNT, sum(CASE WHEN asti.STATUS = 'ROLLBACK' THEN 1 ELSE 0 END) AS ROLLBACK_COUNT, sum(CASE WHEN asti.STATUS = 'FAILED' THEN 1 ELSE 0 END) AS FAILED_COUNT, sum(CASE WHEN asti.STATUS = 'WAIT_TO_BE_PULLED' THEN 1 ELSE 0 END) AS WAIT_TO_BE_PULLED_COUNT FROM asgard_saga_instance asi LEFT OUTER JOIN asgard_saga_task_instance asti ON asi.ID = asti.SAGA_INSTANCE_ID WHERE 1 = 1
AND asi.FD_LEVEL=#{level}
AND asi.SOURCE_ID=#{sourceId}
AND concat(concat(asi.saga_code, '-'), asi.id) LIKE concat(concat('%',#{sagaCode}),'%')
AND (asi.status = #{status} OR asi.status = 'NON_CONSUMER')
AND asi.status = #{status}
AND asi.ref_type LIKE concat(concat('%',#{refType}),'%')
AND asi.ref_id LIKE concat(concat('%',#{refId}),'%')
AND ( concat(concat(asi.saga_code, '-'), asi.id) LIKE concat(concat('%',#{params}),'%') OR asi.ref_type LIKE concat(concat('%',#{params}),'%') OR asi.ref_id LIKE concat(concat('%',#{params}),'%') )
GROUP BY asi.id order by asi.id desc

select sum(case when STATUS='FAILED' then 1 else 0 end) as FAILED_COUNT, sum(case when STATUS='RUNNING' then 1 else 0 end) as RUNNING_COUNT, sum(case when STATUS='COMPLETED' then 1 when STATUS='NON_CONSUMER' then 1 else 0 end) as COMPLETED_COUNT, sum(case when STATUS='ROLLBACK' then 1 else 0 end) as ROLLBACK_COUNT FROM asgard_saga_instance WHERE 1=1
AND FD_LEVEL=#{level}
AND SOURCE_ID=#{sourceId}

SELECT date(asi.CREATION_DATE) AS CREATION_DATE, COUNT(*) AS totalCount, SUM(asi.`status` = 'FAILED') AS failureCount, (SUM(asi.`status` = 'FAILED') / COUNT(*))*100 AS percentage FROM asgard_saga_instance asi WHERE 1=1
AND asi.FD_LEVEL = #{level} AND asi.SOURCE_ID = #{sourceId}
AND asi.CREATION_DATE > #{startTime} AND asi.CREATION_DATE < #{endTime} GROUP BY date(asi.CREATION_DATE)

SELECT
asi.*
FROM
asgard_saga_instance asi
WHERE
asi.`status` = 'FAILED'

    AND asi.FD_LEVEL = 'organization'
    AND asi.SOURCE_ID = #{sourceId}

AND asi.CREATION_DATE > #{startTime}
AND asi.CREATION_DATE <  #{endTime}

mapper.delete:
DELETE FROM asgard_saga_instance
WHERE id IN

    #{item}
